boardCont, boardImg1, boardImg2,
boardImg3, boardImg4
) VALUES (
#{boardCont}, #{boardImg1},
#{boardImg2}, #{boardImg3}, #{boardImg4}
)

mapper.update:
UPDATE board
boardCont = #{boardCont}, boardImg1 = #{boardImg1}, boardImg2 = #{boardImg2}, boardImg3 = #{boardImg3}, boardImg4 = #{boardImg4},
WHERE boardId = #{boardId}
